Insert.RuleBody:
        import it.cnr.isti.labse.glimpse.impl.GlimpseBaseEventImpl;
        import it.cnr.isti.labse.glimpse.manager.ResponseDispatcher;
        import it.cnr.isti.labse.glimpse.utils.NotifierUtils;

        declare GlimpseBaseEventImpl
	        @role( event )
	        @timestamp( timestamp )
        end

		rule "upload time"
		no-loop
		salience 999
		dialect "java"
		when
			$aEvent : GlimpseBaseEventImpl(this.networkedSystemSource =="NS1", this.getConsumed == false);
		then
			$aEvent.setConsumed(true);
			update( $aEvent );
			retract( $aEvent );
			ResponseDispatcher.NotifyMe(drools.getRule().getName(),"myEnabler",NotifierUtils.eventName($aEvent.getName()),NotifierUtils.eventName($aEvent.getData()));
		end
